import Svgmain from "./images/Svgmain.svg";
import { react, useState } from "react";

const { directive } = require("@babel/types");


function Hero()
{
    const [isSpinning, setIsSpinning] = useState(true);

    const stopSpinning = () => {
        setIsSpinning(false);
      };

    return(
        <div className="w-full h-[calc(100vh-150px)]">
            <div className="w-full inline-grid grid-cols-2 h-full p-0 m-0" id="main-hero">
                <div className="w-full flex justify-end my-auto" id="main-svg">
                    <img src={require("./images/placeholderimage.jpg")} className="w-72 h-72"/>
                </div>
                <div className="border-2 h-72 w-76 my-auto mx-4 rounded-3xl transform -translate-x-1/2 -translate-y-1/2 animate-spin animate-moveRight">
                    <p className="fit-content text-5xl mx-4 my-5">
                        Lead the way to saving time with Leader
                    </p>
                </div>
            </div>
        </div>
    );
}

export default Hero;
