import { useState } from "react";

function RangeSlider() {
  const [value1, setValue1] = useState(0);
  const [value2, setValue2] = useState(100);

  function handleChange(e) {
    const value = parseInt(e.target.value);
    if (e.target.id === "thumb1") {
      setValue1(value);
    } else if (e.target.id === "thumb2") {
      setValue2(value);
    }
  }

  return (
    <div className="w-full max-w-md mx-auto">
      <div className="relative w-full h-6 rounded-full bg-gray-400">
        <div
          className="absolute top-0 left-0 w-full h-full rounded-full bg-black"
          style={{
            background: `linear-gradient(to right, black ${value1}%, gray ${value1}%, gray ${value2}%, black ${value2}%)`,
          }}
        ></div>
        <input
          type="range"
          min="0"
          max="100"
          value={value1}
          className="absolute z-10 w-6 h-6 bg-black rounded-full appearance-none thumb1"
          id="thumb1"
          onChange={handleChange}
          style={{
            left: `calc(${value1}% - 10px)`,
            top: "-7px",
          }}
        />
        <div className="absolute z-10 w-24 h-6 text-xs text-center text-white pointer-events-none">
          {value1 * 1000}
        </div>
        <input
          type="range"
          min="0"
          max="100"
          value={value2}
          className="absolute z-10 w-6 h-6 bg-black rounded-full appearance-none thumb2"
          id="thumb2"
          onChange={handleChange}
          style={{
            left: `calc(${value2}% - 10px)`,
            top: "-7px",
          }}
        />
        <div className="absolute z-10 w-24 h-6 text-xs text-center text-white pointer-events-none">
          {value2 * 1000}
        </div>
      </div>
    </div>
  );
}

export default RangeSlider;
